import { parser } from "../dist/index.es.js";
import { expect } from "chai";

function parse(d, fragments) {
  const partialParse = parser.startParse(d, fragments);
  let result = null;
  while (!result) {
    result = partialParse.advance();
  }
  return result;
}

function testNodes(doc1) {
  const cursor = parse(doc1)?.cursor();
  let errorNodeCount = 0;
  let nodeList = '';

  for (;;) {
    nodeList += `${cursor.name}, `;
    if (cursor.type.isError) {
      errorNodeCount += 1;
    }
    const isLeaf = !cursor.firstChild();
    if (!isLeaf) continue;

    for (;;) {
      if (cursor.nextSibling()) break;
      if (!cursor.parent()) {
        return { errorNodeCount, nodeList };
      }
    }
  }
}

describe('Error Node Test', function () {
  it('Should return zero if the query is valid', () => {
    let errorCount;
    let nodes = '';
    try {
      const { errorNodeCount, nodeList } = testNodes(
        // Replace sql text here for rudimentary debugging,
        // a âš  will be printed to indicate the node(s) that failed parse
        `;`,
      );

      errorCount = errorNodeCount;
      nodes = nodeList;
      expect(errorCount).to.equal(0);
    } catch (err) {
      if (err instanceof Error) {
        err.message = `${err.message}\n\nNode list: ${nodes}`;
      }
      throw err;
    }
  });
});
